ader {\n    padding-top: 2.4rem;\n  }\n}\n@media screen and (max-width: 767px) {\n  .start-header {\n    padding-top: 2.4rem;\n  }\n}\n\n.start-header__logo {\n  pointer-events: all;\n  position: absolute;\n  left: 50%;\n  top: 2.7rem;\n  transform: translateX(-50%);\n  width: 2.5rem;\n}\n@media (min-width: 1920px) {\n  .start-header__logo {\n    top: 3.5rem;\n    width: 3.3rem;\n  }\n}\n\n.start-header__logo-image {\n  width: 100%;\n  display: block;\n  height: auto;\n  border: none;\n  outline: none;\n}\n\n.start-header__dots {\n  position: absolute;\n  left: 50%;\n  top: 3.3rem;\n  transform: translateX(-50%);\n}\n@media (min-width: 1920px) {\n  .start-header__dots {\n    top: 4.3rem;\n  }\n}\n@media (max-width: 767px) {\n  .start-header__dots {\n    display: none;\n  }\n}\n\n.start-header__sound {\n  pointer-events: all;\n}","@use './breakpoints';\n@use 'sass:math';\n@use 'sass:map';\n\n@mixin font-face($font-family, $file-path, $weight: 400, $style: normal) {\n  @font-face {\n    font-family: $font-family;\n    src: url('#{$file-path}.otf');\n    // src: url('#{$file-path}.eot?#iefix') format('embedded-opentype'), url('#{$file-path}.woff') format('woff'), url('#{$file-path}.ttf') format('truetype');\n    font-weight: $weight;\n    font-style: $style;\n    font-display: swap;\n  }\n}\n\n\n@mixin paddingRatio($width, $height) {\n  padding-top: calc(#{$height} / #{$width} * 100%);\n}\n\n\n@mixin scale($property, $max-width, $min-width, $max-size, $min-size) {\n  #{$property}: calc(\n    #{$max-size} - (#{$max-width} - var(--viewport-width)) / (\n        #{$max-width} - #{$min-width}\n      ) * (#{$max-size} - #{$min-size})\n  );\n}\n\n@mixin mscale($max-width, $min-width, $map) {\n  @each $property, $value in $map {\n    #{$property}: calc(\n      #{nth($value, 1)} -\n        (#{$max-width} - var(--viewport-width)) /\n        (#{$max-width} - #{$min-width}) *\n        (#{nth($value, 1)} - #{nth($value, 2)})\n    );\n  }\n}\n//---------------------------  Offsets table ------------------------------------\n\n// Get offset size from table\n\n@function foffset($color, $min-viewport) {\n  $color-map: map.get($offsets, $color);\n  @if map.has-key($color-map, $min-viewport) {\n    @return map.get($color-map, $min-viewport);\n  }\n}\n\n@mixin offset-media($prop, $color) {\n  #{$prop}: foffset($color, bg);\n  @media screen and (min-width: (map.get(breakpoints.$breakpoints-min, lg) * 1px)) {\n    #{$prop}: foffset($color, lg);\n  }\n  @media screen and (max-width: (map.get(breakpoints.$breakpoints-max, bg) * 1px)) {\n    #{$prop}: foffset($color, sm);\n  }\n  @media screen and (max-width: (map.get(breakpoints.$breakpoints-max, sm) * 1px)) {\n    #{$prop}: foffset($color, xs);\n  }\n  @media screen and (max-width: (map.get(breakpoints.$breakpoints-max, xs) * 1px)) {\n    #{$prop}: foffset($color, xxs);\n  }\n}\n\n@mixin max($size) {\n  @if map.has-key($breakpoints-max, $size) {\n    @media (max-width: #{map.get($breakpoints-max, $size)}px) {\n      @content;\n    }\n  } @else {\n    @media (max-width: #{$size}px) {\n      @content;\n    }\n  }\n}\n\n@mixin min($size) {\n  @if map.has-key($breakpoints-min, $size) {\n    @media (min-width: #{map.get($breakpoints-min, $size)}px) {\n      @content;\n    }\n  } @else {\n    @media (min-width: #{$size}px) {\n      @content;\n    }\n  }\n}\n\n@mixin media($property,$normal, $lg, $bg, $sm, $xs) {\n  #{$property}: $normal;\n  @include min(lg) {\n    #{$property}: $lg\n  }\n  @include max(bg) {\n    #{$property}: $bg;\n  }\n  @include max(sm) {\n    #{$property}: $sm;\n  }\n  @include max(xs) {\n    #{$property}: $xs;\n  }\n}\n\n@mixin h3 {\n  line-height: 1;\n  font-size: 4.7rem;\n  font-weight: 400;\n  @include min(lg) {\n    font-size: 5.7rem;\n  }\n  @include max(bg) {\n    font-size: 3.4rem;\n  }\n  @include max(sm) {\n    font-size: 3.4rem;\n  }\n  @include max(xs) {\n    font-size: 2.4rem;\n  }\n}\n\n@mixin p {\n  font-family: var(--font-mono-regular);\n  font-size: 1.5rem;\n  font-weight: 400;\n  line-height: 1.3;\n  @include min(lg) {\n    font-size: 1.7rem;\n  }\n}\n\n"],"sourceRoot":""}]);
