ta.states.length)\n\n      if (!data.allDone) {\n        this.building = true\n      } else {\n        this.$nextTick(() => {\n          this.building = false\n          this.animatedProgress = 0\n          this.progress = 0\n          clearInterval(this._progressAnimation)\n        })\n      }\n    },\n\n    sseClose () {\n      if (this.sse) {\n        this.sse.close()\n        delete this.sse\n      }\n    }\n  }\n}\n</script>\n\n<style scoped>\n.nuxt__build_indicator {\n  box-sizing: border-box;\n  position: fixed;\n  font-family: monospace;\n  padding: 5px 10px;\n  border-radius: 5px;\n  box-shadow: 1px 1px 2px 0px rgba(0,0,0,0.2);\n  width: 88px;\n  z-index: 2147483647;\n  font-size: 16px;\n  line-height: 1.2rem;\n}\n.v-enter-active, .v-leave-active {\n  transition-delay: 0.2s;\n  transition-property: all;\n  transition-duration: 0.3s;\n}\n.v-leave-to {\n  opacity: 0;\n  transform: translateY(20px);\n}\nsvg {\n  display: inline-block;\n  vertical-align: baseline;\n  width: 1.1em;\n  height: 0.825em;\n  position: relative;\n  top: 1px;\n}\n</style>\n"],"sourceRoot":""}]);
