perty}: $bg;\n  }\n  @include max(sm) {\n    #{$property}: $sm;\n  }\n  @include max(xs) {\n    #{$property}: $xs;\n  }\n}\n\n@mixin h3 {\n  line-height: 1;\n  font-size: 4.7rem;\n  font-weight: 400;\n  @include min(lg) {\n    font-size: 5.7rem;\n  }\n  @include max(bg) {\n    font-size: 3.4rem;\n  }\n  @include max(sm) {\n    font-size: 3.4rem;\n  }\n  @include max(xs) {\n    font-size: 2.4rem;\n  }\n}\n\n@mixin p {\n  font-family: var(--font-mono-regular);\n  font-size: 1.5rem;\n  font-weight: 400;\n  line-height: 1.3;\n  @include min(lg) {\n    font-size: 1.7rem;\n  }\n}\n\n"],"sourceRoot":""}]);
